SELECT lidesired, lipurchased, round((1 - (lidesired - lipurchased)/CAST (lidesired AS numeric)) * 100, 2) || '%' AS "Fulfillment" FROM listitems ORDER BY round((1 - (lidesired - lipurchased)/CAST (lidesired AS numeric)) * 100, 2) DESC;
